import React, {PropTypes} from 'react';
import classNames from 'classnames';

import Button from '@ciscospark/react-component-button';

import styles from './styles.css';

export default function ActivityPostAction(props) {
  return (
    <div className={classNames(`ciscospark-post-action-item`, styles.postActionItem)}>
      <Button iconType={props.iconType} onClick={props.onClick} title={props.title} />
    </div>
  );
}

ActivityPostAction.propTypes = {
  iconType: PropTypes.string.isRequired,
  onClick: PropTypes.func.isRequired,
  title: PropTypes.string
};
