import React, {PropTypes} from 'react';
import classNames from 'classnames';
import Button from '@ciscospark/react-component-button';

import styles from './styles.css';

export default function ButtonControls(props) {
  const {
    buttons,
    showLabels
  } = props;

  const buttonsRendered = buttons.map((button, index) => {
    const label = showLabels && button.label ? button.label : ``;
    const labelPosition = showLabels ? `bottom` : ``;
    return (
      <div className={classNames(`ciscospark-button-control-item`, styles.controlItem)} key={index}>
        <Button
          {...button}
          buttonClassName={classNames(button.buttonClassName, styles.button)}
          label={label}
          labelPosition={labelPosition}
        />
      </div>
    );
  });

  return (
    <div className={classNames(`ciscospark-controls-container`, styles.controlContainer)}>
      {buttonsRendered}
    </div>
  );
}

ButtonControls.propTypes = {
  buttons: PropTypes.arrayOf(PropTypes.shape({
    label: PropTypes.string,
    buttonClassName: PropTypes.string,
    iconType: PropTypes.string,
    onClick: PropTypes.func
  })),
  showLabels: PropTypes.bool
};
