import React from 'react';
import PropTypes from 'prop-types';

import classNames from 'classnames';

import styles from './styles.css';

export const ICON_TYPE_ADD = `icon-cisco-add`;
export const ICON_TYPE_DELETE = `icon-cisco-exit-outline`;
export const ICON_TYPE_DOCUMENT = `icon-cisco-document-outline`;
export const ICON_TYPE_DOWNLOAD = `icon-cisco-download`;
export const ICON_TYPE_EXTERNAL_USER = `icon-cisco-external-user`;
export const ICON_TYPE_EXTERNAL_USER_OUTLINE = `icon-cisco-external-user-outline`;
export const ICON_TYPE_FLAGGED = `icon-cisco-flagged`;
export const ICON_TYPE_FLAGGED_OUTLINE = `icon-cisco-flagged-outline`;
export const ICON_TYPE_MESSAGE = `icon-cisco-message`;
export const ICON_TYPE_MESSAGE_OUTLINE = `icon-cisco-message-outline`;
export const ICON_TYPE_RIGHT_ARROW = `icon-cisco-right-arrow`;
export const ICON_TYPE_VIDEO_OUTLINE = `icon-cisco-video-outline`;
export const ICON_TYPE_VIDEO_CROSS_OUTLINE = `icon-cisco-video-cross-outline`;
export const ICON_TYPE_MUTE_OUTLINE = `icon-cisco-mute-outline`;
export const ICON_TYPE_EXIT = `icon-cisco-exit`;
export const ICON_TYPE_WAFFLE = `icon-cisco-waffle-pad`;

function Icon(props) {
  return (
    <div className={classNames(`ciscospark-icon`, styles.icon, styles[props.type])} title={props.title} />
  );
}

Icon.propTypes = {
  title: PropTypes.string,
  type: PropTypes.string.isRequired
};

export default Icon;
