import React, {PropTypes} from 'react';
import classNames from 'classnames';

import Button from '@ciscospark/react-component-button';
import Icon, {ICON_TYPE_RIGHT_ARROW} from '@ciscospark/react-component-icon';

import styles from './styles.css';

export default function ScrollToBottomButton(props) {
  const {
    onClick,
    label
  } = props;

  let containerStyle, labelSpan;

  if (label) {
    labelSpan = <span className={classNames(`ciscospark-scroll-to-bottom-label`, styles.label)}>{label}</span>;
    containerStyle = styles.withText;
  }

  return (
    <div className={classNames(`ciscospark-scroll-to-bottom-container`, styles.container, containerStyle)}>
      <Button onClick={onClick}>
        <div className={classNames(`ciscospark-scroll-to-bottom-icon`, styles.icon)}>
          <Icon type={ICON_TYPE_RIGHT_ARROW} />
        </div>
        {labelSpan}
      </Button>
    </div>
  );
}

ScrollToBottomButton.propTypes = {
  label: PropTypes.string,
  onClick: PropTypes.func
};
