import React, {PropTypes} from 'react';
import {addLocaleData, IntlProvider} from 'react-intl';
import enLocaleData from 'react-intl/locale-data/en';

import Meet from './container';
import enTranslationMessages from './translations/en';


export default function MeetWidgetWithIntl(props) {
  addLocaleData(enLocaleData);
  return (
    <IntlProvider locale="en" messages={enTranslationMessages}>
      <Meet {...props} />
    </IntlProvider>
  );
}

MeetWidgetWithIntl.propTypes = {
  onEvent: PropTypes.func,
  shouldStartMeeting: PropTypes.bool,
  spark: PropTypes.object.isRequired,
  toPersonAvatar: PropTypes.string,
  toPersonEmail: PropTypes.string,
  toPersonId: PropTypes.string,
  toPersonName: PropTypes.string.isRequired
};
