import React from 'react';
import renderer from 'react-test-renderer';

import ParticipantListItem from '.';

describe(`ParticipantListItem component`, () => {
  it(`renders a local participant properly`, () => {
    const participant = {
      avatar: `http://google.com/image.png`,
      displayName: `Dr Dre`,
      id: `1`
    };
    const component = renderer.create(
      <ParticipantListItem participant={participant} />
    );
    expect(component).toMatchSnapshot();
  });

  it(`renders an external participant properly`, () => {
    const participant = {
      avatar: `http://google.com/image-2.png`,
      displayName: `Jimmy Iovine`,
      external: true,
      id: `2`
    };
    const component = renderer.create(
      <ParticipantListItem participant={participant} />
    );
    expect(component).toMatchSnapshot();
  });
});
