import React from 'react';
import renderer from 'react-test-renderer';

import ParticipantList from '.';

describe(`ParticipantList component`, () => {
  it(`renders empty array properly`, () => {
    const component = renderer.create(
      <ParticipantList participants={[]} />
    );
    expect(component).toMatchSnapshot();
  });

  it(`renders an array of participants`, () => {
    const participants = [
      {
        avatar: `http://google.com/image.png`,
        displayName: `Dr Dre`,
        id: `1`
      },
      {
        avatar: `http://google.com/image-2.png`,
        displayName: `Jimmy Iovine`,
        external: true,
        id: `2`
      }
    ];
    const component = renderer.create(
      <ParticipantList participants={participants} />
    );
    expect(component).toMatchSnapshot();
  });
});
