import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';

import {WebexInMeeting, WebexMeetingControls, WebexMeetingControl, WebexDataProvider} from '@webex/components';

import styles from './styles.css';

const ActiveMeeting = ({adapter, meetingId}) => (
  <WebexDataProvider adapter={adapter}>
    <div className={classNames(styles.callContainer, 'call-container')}>
      <WebexMeetingControls className={styles.callControls} meetingID={meetingId}>
        <WebexMeetingControl type="mute-audio" />
        <WebexMeetingControl type="mute-video" />
        <WebexMeetingControl type="leave-meeting" />
      </WebexMeetingControls>
      <WebexInMeeting meetingID={meetingId} />
    </div>
  </WebexDataProvider>
);

ActiveMeeting.propTypes = {
  adapter: PropTypes.object.isRequired,
  meetingId: PropTypes.string.isRequired
};

export default ActiveMeeting;
